import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Home, PlusCircle, XCircle, Edit2, Trash2 } from 'lucide-react';
import NewExpenseForm from './NewExpense';
import { Link } from 'react-router-dom';

const HomePage = () => {
  const [expenses, setExpenses] = useState([]);

  useEffect(() => {
    fetchExpenses();
  }, []);

  const fetchExpenses = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/expenses');
      setExpenses(response.data);
    } catch (error) {
      console.error('Error fetching expenses:', error);
    }
  };

  const [form, setHide] = useState(true);

  const showForm = () => {
    setHide(!form);
  };

  return (
    <div className='main'>
      <div className='main-header'>
        <Home className='align-svg-home'/>
        <p>Expense Tracker</p>
        <PlusCircle className='align-svg-plus' onClick={showForm}/>
      </div>
      <h2>Balance: ₹{balance.toFixed(2)}</h2>
      <NewExpenseForm fetchExpenses={fetchExpenses} />
      <h2>Expenses</h2>
      <ul className='expense-list'>
        {expenses.slice().reverse().map(expense => (
          <li key={expense._id}>
            <div>
              {expense.title}
            </div>
            <div id='expAmount' className='green-font'>
              ₹ {expense.amount}
            </div>
            <div>
              <Edit2 to="/edit" onClick={() => handleEdit(expense._id)}/>
            </div>
            <div>
              <Trash2 onClick={() => handleDelete(expense._id)}/>
            </div>
             {/* - ₹ - {expense.category} - {expense.type}  onLoad={getExpenseType(expense.type)}*/}
          </li>
        ))}
      </ul>
      <Link to="/edit">Edit Expense</Link> {/* Link to edit expense form */}
    </div>
  );
};

export default HomePage;
