import React, { useState, useEffect } from 'react';
import { Home, PlusCircle, CreditCard, XCircle } from 'lucide-react';
import axios from 'axios';

const HomePage = () => {
  const [showAdd, setShowAdd] = useState(false);
  const [balance, setBalance] = useState(0);
  const [latestTransactions, setLatestTransactions] = useState([]);

  useEffect(() => {
    fetchBalance();
    fetchLatestTransactions();
  }, []);

  const toggleAddForm = () => {
    setShowAdd(!showAdd);
  };

  const fetchBalance = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/balance');
      setBalance(response.data.balance);
    } catch (error) {
      console.error('Error fetching balance:', error);
    }
  };

  const fetchLatestTransactions = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/transactions');
      setLatestTransactions(response.data.transactions);
    } catch (error) {
      console.error('Error fetching latest transactions:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const newExpense = {
      title: formData.get('title'),
      amount: formData.get('amount'),
      category: formData.get('category'),
      type: formData.get('mode')
    };
    try {
      await axios.post('http://localhost:5000/api/expenses', newExpense);
      toggleAddForm();
      fetchBalance();
      fetchLatestTransactions();
    } catch (error) {
      console.error('Error adding expense:', error);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-blue-500 text-white text-lg py-4 px-6 flex justify-between items-center">
        <Home className="w-6 h-6 cursor-pointer" />
        <h1 className="text-xl">Expense Tracker</h1>
        <PlusCircle className="w-6 h-6 cursor-pointer" onClick={toggleAddForm} />
      </header>
      {showAdd && (
        <div className="bg-white p-6">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-gray-700 text-lg">Add New Expense</h2>
            <XCircle className="w-6 h-6 text-gray-700 cursor-pointer" onClick={toggleAddForm} />
          </div>
          <form onSubmit={handleSubmit}>
            <div className="mb-4">
              <label className="block text-gray-700 text-sm mb-2" htmlFor="title">
                Title
              </label>
              <input className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" id="title" type="text" placeholder="Title" name="title" required />
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 text-sm mb-2" htmlFor="amount">
                Amount
              </label>
              <input className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" id="amount" type="number" placeholder="Amount" name="amount" required />
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 text-sm mb-2" htmlFor="mode">
                Mode of Transaction
              </label>
              <select className="shadow border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" id="mode" name="mode">
                <option value="Credit">Credit</option>
                <option value="Debit">Debit</option>
              </select>
            </div>
            <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline" type="submit">
              Submit
            </button>
          </form>
        </div>
      )}
      <main className="p-6">
        <section className="bg-white shadow-md rounded-md p-6 mb-6">
          <h2 className="text-gray-700 text-lg mb-4">Balance</h2>
          <p className="text-3xl text-green-500">${balance.toFixed(2)}</p>
        </section>
        <section className="bg-white shadow-md rounded-md p-6">
          <h2 className="text-gray-700 text-lg mb-4">Latest Transactions</h2>
          <ul>
            {latestTransactions.map((transaction, index) => (
              <li key={index} className="flex justify-between items-center py-3 border-b">
                <span className="flex items-center">
                  <CreditCard className={`w-5 h-5 mr-3 ${transaction.type === 'Debit' ? 'text-red-500' : 'text-green-500'}`} />
                  {transaction.title}
                </span>
                <span className={transaction.type === 'Debit' ? 'text-red-500' : 'text-green-500'}>
                  {transaction.type === 'Debit' ? '-' : '+'}${transaction.amount.toFixed(2)}
                </span>
              </li>
            ))}
          </ul>
        </section>
      </main>
    </div>
  );
};

export default HomePage;
