import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';

const EditExpenseForm = ({ expenses }) => {
  const { id } = useParams(); // Extract the expense ID from the URL
  const history = useHistory();

  const [formData, setFormData] = useState({
    title: '',
    amount: '',
    category: '',
    type: 'Debit'
  });

  useEffect(() => {
    // Fetch the expense details based on the ID
    const expense = expenses.find(expense => expense._id === id);
    if (expense) {
      setFormData({
        title: expense.title,
        amount: expense.amount,
        category: expense.category,
        type: expense.type
      });
    }
  }, [id, expenses]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

    // const handleEdit = async (id) => {
    // // Fetch the expense to be edited
    // console.log("handleEdit ----------");
    // console.log("id: ",id);
    // const expenseToEdit = expenses.find(expense => expense._id === id);
    // console.log("expenseToEdit: ", expenseToEdit);
    // if (!expenseToEdit) {
    //   console.error('Expense not found');
    //   return;
    // }

  // Prepare updated expense data
  const handleSubmit = async (e) => {
    e.preventDefault();
    const expenseToEdit = expenses.find(expense => expense._id === id);

    try{
        const updatedExpense = {
        ...expenseToEdit,
        title: newTitle,
        amount: newAmount,
        category: newCategory,
        type: newType
        };
        console.log("updatedExpense: ", updatedExpense);

        // Make API call to update expense
        try {
        await axios.put(`http://localhost:5000/api/expenses/${id}`, updatedExpense);
        fetchExpenses(); // Refresh the expense list after editing
        } catch (error) {
        console.error('Error editing expense:', error);
        }
        history.push('/');
    } catch {
        if (!expenseToEdit) {
            console.error('Expense not found');
            return;
          }
    }

  };

  return (
    <div>
      <h2>Edit Expense</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Title:
          <input type="text" name="title" value={formData.title} onChange={handleInputChange} required />
        </label>
        <label>
          Amount:
          <input type="number" name="amount" value={formData.amount} onChange={handleInputChange} required />
        </label>
        <label>
          Category:
          <input type="text" name="category" value={formData.category} onChange={handleInputChange} required />
        </label>
        <label>
          Type:
          <select name="type" value={formData.type} onChange={handleInputChange}>
            <option value="Debit">Debit</option>
            <option value="Credit">Credit</option>
          </select>
        </label>
        <button type="submit">Save Changes</button>
      </form>
    </div>
  );
};

export default EditExpenseForm;
