import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';


const EditExpenseForm = ({ expenses, fetchExpenses }) => {
  const { id } = useParams(); // Extract the expense ID from the URL
//   const history = useHistory();

  const [formData, setFormData] = useState({
    title: '',
    amount: '',
    category: '',
    type: 'Debit'
  });

  useEffect(() => {
    // Fetch the expense details based on the ID
    const fetchExpenseDetails = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/expenses/${id}`);
        const data = await response.json();
        setFormData({
          title: data.title,
          amount: data.amount,
          category: data.category,
          type: data.type
        });
      } catch (error) {
        console.error('Error fetching expense details:', error);
      }
    };

    fetchExpenseDetails();
  }, [id]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  // Prepare updated expense data
  const handleSubmit = async (e) => {
    e.preventDefault();
    const updatedExpense = {
      ...formData,
      _id: id // Ensure the ID is included in the updated expense data
    };

    try {
      // Make API call to update expense
      await axios.put(`http://localhost:5000/api/expenses/${id}`, updatedExpense);
      fetchExpenses(); // Refresh the expense list after editing
    //   history.push('/');
    } catch (error) {
      console.error('Error editing expense:', error);
    }

  };

  return (
    <div>
      <h2>Edit Expense</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Title:
          <input type="text" name="title" value={formData.title} onChange={handleInputChange} required />
        </label>
        <label>
          Amount:
          <input type="number" name="amount" value={formData.amount} onChange={handleInputChange} required />
        </label>
        <label>
          Category:
          <input type="text" name="category" value={formData.category} onChange={handleInputChange} required />
        </label>
        <label>
          Type:
          <select name="type" value={formData.type} onChange={handleInputChange}>
            <option value="Debit">Debit</option>
            <option value="Credit">Credit</option>
          </select>
        </label>
        <button type="submit">Save Changes</button>
      </form>
    </div>
  );
};

export default EditExpenseForm;



