import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [expenses, setExpenses] = useState([]);
  const [newExpense, setNewExpense] = useState({
    title: '',
    amount: '',
    category: '',
    type: 'Debit' // Default type is Debit
  });
  const [balance, setBalance] = useState(0);

  useEffect(() => {
    fetchExpenses();
  }, []);

  useEffect(() => {
    calculateBalance();
  }, [expenses]);

  const fetchExpenses = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/expenses');
      setExpenses(response.data);
    } catch (error) {
      console.error('Error fetching expenses:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewExpense(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/expenses', newExpense);
      setNewExpense({ title: '', amount: '', category: '', type: 'Debit' });
      fetchExpenses(); // Refresh the expense list after adding a new expense
    } catch (error) {
      console.error('Error adding expense:', error);
    }
  };

  const handleEdit = async (id) => {
    // Implement edit functionality here
  };

  const handleDelete = async (id) => {
    // Implement delete functionality here
  };

  const calculateBalance = () => {
    const totalCredit = expenses
      .filter(expense => expense.type === 'Credit')
      .reduce((acc, curr) => acc + parseFloat(curr.amount), 0);
    const totalDebit = expenses
      .filter(expense => expense.type === 'Debit')
      .reduce((acc, curr) => acc + parseFloat(curr.amount), 0);
    setBalance(totalCredit - totalDebit);
  };

  return (
    <div>
      <h1>Expense Tracker</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Title:
          <input type="text" name="title" value={newExpense.title} onChange={handleInputChange} required />
        </label>
        <label>
          Amount:
          <input type="number" name="amount" value={newExpense.amount} onChange={handleInputChange} required />
        </label>
        <label>
          Category:
          <input type="text" name="category" value={newExpense.category} onChange={handleInputChange} required />
        </label>
        <label>
          Type:
          <select name="type" value={newExpense.type} onChange={handleInputChange}>
            <option value="Debit">Debit</option>
            <option value="Credit">Credit</option>
          </select>
        </label>
        <button type="submit">Add Expense</button>
      </form>
      <h2>Balance: ${balance.toFixed(2)}</h2>
      <h2>Expenses</h2>
      <ul>
        {expenses.map(expense => (
          <li key={expense._id}>
            {expense.title} - ${expense.amount} - {expense.category} - {expense.type}
            <button onClick={() => handleEdit(expense._id)}>Edit</button>
            <button onClick={() => handleDelete(expense._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
